/* Black & Green Style for Goosh.org - by James Burgess (www.jamesburgess.co.uk) */
/* v1.03 - 04-Jun-2008 @ 10:27 BST */

@namespace url(http://www.w3.org/1999/xhtml);

body, #output {
background-color: #000000 !important;
color: #00FF00 !important;
}

.info, #prompt, td, .inputb { color:#090 !important; }
.less { color:#0F0 !important; }

a { text-decoration: underline !important; }
a:link { color: #00FF00 !important; }
a:visited { color: #00FF00 !important; }
a:active { color: #CCC !important; }
a:hover { color:#FF0 !important; }

td a { text-decoration: none !important; }
td a:before { content: '- '; }
td a:first-child { text-decoration: underline !important; }
td a:first-child:before { content:'' !important; }

#inputfield {
color: #00FF00 !important;
background-color: #000000 !important;
}

div.input{
color: #0F0 !important;
background-color:#000000;
}

:root {
    --dark1:    #151515;
    --dark2: #404040;
    --accent1:  #30f246;
    --accent2: #a1d979;
}


html, body {
    background: none;
    background-color: var(--dark1);
    color: white;
}

input[type="button"], input[type="submit"], #qr-filename-container, #fourchanx-settings, select[data-name="thread"], .dialog {
    background: none !important;
	background-color: var(--dark2) !important;
    border-color: var(--accent1) !important;
}

.post.reply, .catalog-post, .fourchanx-settings, .reply-to-thread, textarea, input, .inline {
    background-color: var(--dark2) !important;
    border-color: var(--accent1) !important;
}

a.qr-link {
    background: none !important;
}

select[data-name="thread"] {
    border-width: 0px !important;
}


#shortcuts, select[data-name="thread"], .absBotDisclaimer, #footer-links, a:hover, .rules {
    color: white !important;
}

#board-list, div.backlink, a, .quotelink, .quoteLink, .deadlink span.postNum.desktop, ::placeholder, #qr-no-file, #index-sort, #index-size, #index-mode{
    color: var(--accent2) !important;
}

span.quote, span.name, div.boardTitle, span.subject, textarea, input, a.backlink{
	color: var(--accent1) !important;
}


.stylechanger, #absbot, a.watch-thread-link {
    visibility: hidden;
}

#index-sort, #index-size, #index-mode {
    background: none;
    border-color: var(--accent) !important;
}

.burichan_new .backlink a, .yotsuba_b_new .backlink a{
color: var(--accent2) !important;
}

.burichan_new .backlink a:hover, .yotsuba_b_new .backlink a:hover{
color: var(--accent1) !important;

}
::-moz-selection {
    background: #9bba86 !important;
    color: white !important;
}


::-webkit-scrollbar, ::-webkit-scrollbar-corner {
		background: var(--dark1);
		width: 14px;
		height: 14px;
}
::-webkit-scrollbar-button {
		display: none;
}
::-webkit-scrollbar-thumb {
		background: var(--dark2);
}
::-webkit-scrollbar-thumb:not(:active):hover {
		background: #383838;
}
::-webkit-scrollbar-thumb:active, ::-wekit-scrollbar-thumb:hover:active {
		background: #5c5c5c;
}

@namespace url(http://www.w3.org/1999/xhtml);
body,
.modal-content {
    color: #bbb !important;
    background: #252525 !important
}
a.tg_checkbox {
    color: #bbb !important;
}
.im_page_wrap {
    /*background color of contentwrapper under header*/
    
    background-color: #303030 !important;
    border-left: 1px solid #4d4d4d !important;
    border-right: 1px solid #4d4d4d !important;
    border-bottom: 1px solid #4d4d4d !important;
    box-shadow: none;
}

.im_message_body {
    background: inherit !important;
}

.im_message_document_thumbed {
    background: inherit !important;
}

.im_dialog_peer {
    /*peer nickname unselected*/
    
    color: #bbb !important;
}
.contacts_modal_members_list a.contacts_modal_contact {
    /*nicknames in contactlist via hamburger menu*/
    
    color: #bbb;
}
.active a.im_dialog {
    /*selected peer */
    
    background-color: #4d4d4d !important;
}
.tg_head_split,
.md_modal_head {
    /*page header block*/
    
    background-color: #4d4d4d !important;
}
.dropdown.open .tg_head_btn,
.tg_head_btn:hover {
    background: #4d4d4d !important;
}
.form-control {
    /*input field*/
    background-color: #0e0e0e !important;
    color: #bbb !important;
}
.composer_rich_textarea {
        background: #303030 !important;
        border-color: #bbb !important;
}
.im_dialogs_col_wrap {
    /*contactlist right border*/
    
    border-right: 2px solid #4d4d4d;
}
.im_dialogs_col .nano > .nano-pane > .nano-slider {
    /*scrollbarbutton when hover*/
    
    background-color: #4d4d4d !important;
}
.tg_head_split {
    background-color: #151515;
}
.im_submit {
    color: #4d4d4d;
}
.im_message_mymention {
    background: #1d1d1d;
}
.im_message_focus .im_message_outer_wrap {
    background-color: #4d4d4d;
}
.im_message_selected .im_message_outer_wrap {
    background: #4d4d4d;
}
a:hover {
    background-color: #282828 !important;
}
.im_message_unread_split {
    background: #4d4d4d;
}
@keyframes im_message_focus_fade {
    0% {
        /*soft transaction*/
        
        background-color: #2d2d2d;
    }
}
.btn-primary {
    background-color: #4d4d4d;
}
.im_history_select_active .im_message_outer_wrap:hover {
    background: #4d4d4d;
}
.im_dialog_chat_from_wrap,
.im_dialog_message_service {
    color: #6ac;
}
.composer_rich_textarea,
.composer_textarea {
    box-shadow: 0px 3px 1px #ddd;
}
.composer_rich_textarea:focus {
    box-shadow: 0px 3px 1px #ddd !important;
}
.tg_head_logo_dropdown .dropdown-menu > li > a {
    color: #ddd;
}
.btn-primary.disabled,
.btn-primary[disabled] {
    background: #4d4d4d none repeat scroll 0% 0%;
}
/*emoji*/

.composer_emoji_tooltip,
.icon-tooltip-tail,
.dropdown-menu {
    background-color: #4d4d4d !important;
    border-color: #5d5d5d;
}

@namespace url(http://www.w3.org/1999/xhtml);
/* *** Basic *** */

*{
   background-color: transparent !important;
   color: #a1d979 !important;
}

*{
	border-color: #30f256 !important;
}

body, div, form, table, tbody, tr, td, fieldset, ul, li, a,
#content, #bodyContent{
   background-color: #151515 !important;
   background-image: none !important;
}


/* *** Form Elements *** */
input, select, button, checkbox{
   -moz-appearance: none !important;
   border: 1px white solid !important;
   outline: none !important;
   background-color: #151515 !important;
}

#simpleSearch{
   border: none !important;
}


/* *** Main *** */
#content{
   font-family: trebuchet, arial !important; 
   font-size: 12pt !important;
   border: 0 !important;
}

div#siteNotice{
   display:none !important;
}
p{
   border: none !important;
}
a{
   color: #30f246 !important;
}
a:visited{
   font-weight: bold !important;
   opacity: 0.65 !important;
}
a:link{
   color: #30f246 !important;
}

h2{
   border-bottom: #30f256 1px solid !important;
   font-size: 120% !important;
}
h2 > span{
   color: #30f246 !important;
}
h2 > span > span{
   color: #30f246 !important;
   font-size: 100% !important;
}

fieldset{
   border-color: #151515 !important;
}

#bodyContent ul{
   list-style-image: none !important;
   list-style-type: disc !important;
}
#toc ul{
   list-style-type: none !important;
}

.thumb{
   border-color: #151515 !important;
}


/* *** Tabs *** */
#searchButton{
   display: none !important;
}

div.vectorTabs,
div.vectorTabs ul,
div.vectorTabs ul > li,
div.vectorTabs ul > li > a{
   background-color: #151515 !important;
   background-image: none !important;
   border: none !important;
}

#right-navigation > .vectorMenu,
#ca-watch{
   display: none !important;
}

div.vectorTabs ul > li > a > span{
   padding: 0 !important;
}
div.vectorTabs ul > li > a{
  outline: 1px #888 solid !important;
  margin: 0 0.5em;
  height: auto !important;
  font-weight: normal !important;
}
div.vectorTabs ul > li > a:hover{
   text-decoration: none !important;
   outline: 1px #ccc solid !important;
}


/* *** Preference *** */
#preftoc > li{
   outline: 1px #151515 solid !important;
   border-bottom: 5px #151515 solid !important;
}

.successbox{
   border-color: #30f246 !important;
}


/* *** Search *** */
#search .search-types ul,
#search .results-info ul{
   list-style-type: none !important;
}


/* *** Logo Replacement *** */
#p-logo a {
   background: url(http://upload.wikimedia.org/wikipedia/en/b/bc/Wiki.png) no-repeat !important;
   position: absolute !important;
   clip: rect(0, 135px, 115px, 0) !important;
   height: 115px !important;
   overflow: hidden !important;
   margin-top: 30px !important;
}


/* *** Image and Math *** */
img{
   background-color: #404040 !important;
}
img.tex{
   padding: 5px !important;
}
span.tex{
   border: 1px white solid !important;
   padding: 5px !important;
}

button{
    border: none !important;
}
::selection {
    background: #9bba86 !important;
    color: white !important;
}

/*
     * Name:    DarkSearch for Google
     * Author:  Nass O
     * Version: v2.1.8
     */

    /* Background */
    html, body, #cnt, .rg_l, #botabar {
        background: #101010 !important;
        color: #c5c5c5;
    }
    /* Inverting colors */
    .qbh, .s2tb .google-logo, .gb_Vb, #wob_gsp, ._kac, #qbi, .ab_icon, .gsok_a, ._Fnc, ._Jxg, .stt-uf, ._dVi, .kcb.checked::after, ._gW {
        -webkit-filter: invert(1) grayscale(1) !important;
        -moz-filter: invert(1) grayscale(1) !important;
        filter: invert(1) grayscale(1) !important;
    }
    ._Zgf {
        -webkit-filter: invert(.6) grayscale(1) !important;
        -moz-filter: invert(.6) grayscale(1) !important;
        filter: invert(.6) grayscale(1) !important;
    }
    .gsri_a, .sbico {
        -webkit-filter: grayscale(.6) !important;
        -moz-filter: grayscale(.6) !important;
        filter: grayscale(.6) !important;
    }
    ._n0b, ._MNk._wtf svg {
        -webkit-filter: invert(.9) !important;
        -moz-filter: invert(.9) !important;
        filter: invert(.9) !important;
    }
    #qbx {
        -webkit-filter: invert(.3) !important;
        -moz-filter: invert(.3) !important;
        filter: invert(.3) !important;
    }
    /* Images opacity */
    #hplogo, #logo img, .gbii {
        opacity: .7;
        transition: opacity .2s;
    }
    #hplogo:hover, #logo img:hover, .gbii:hover {
        opacity: 1;
        transition: opacity .5s;
    }

    /* Scrollbar Chrome*/
    *::-webkit-scrollbar {
        width: 10px !important;
        height: 10px !important;
        background: #1e1e1e !important;
        border: 1px solid #252525 !important;
    }
    *::-webkit-scrollbar-button {
        display: none !important;
    }
    *::-webkit-scrollbar-thumb,
    *::-webkit-scrollbar-track {
        border: 0 !important;
        box-shadow: none !important;
    }
    *::-webkit-scrollbar-thumb {
        min-height: 28px !important;
        background: #333 !important;
    }
    *::-webkit-scrollbar-corner,
    *::-webkit-scrollbar-track {
        background: #1e1e1e !important;
    }

    /* Text Input #C5C5C5 */
    input, #lst-ib, select {
        color: #C5C5C5 !important;
    }
    input {
        background-color: #222;
    }

    /* Auto completion
    input#lst-ib.gsfi {
        background: url(data:image/gif;base64,R0lGODlhAQABAID/AMDAwAAAACH5BAEAAAAALAAAAAABAAEAAAICRAEAOw%3D%3D) rgba(34, 34, 34, 0.74)!important;

    }
    */

    /* Header */
    .sfbgg {
        background-color: #151515 !important;
        border-bottom: none !important;
        background-image: none;
    }
    .sfbgx {
        background-color: #151515 !important;
        border-bottom-color: #222 !important;
    }
    .mw, ._PJ {
        background: #101010 !important;
    }

    /* Footer */
    #fbar {
        background: #1d1d1d !important;
        border-top: 1px solid #252525 !important;
    }

    /* Search options bar */
    #hdtbSum {
        background: #151515 !important;
        border-bottom: 1px solid #1d1d1d !important;
    }
    #appbar, #hdtbMenus, #hdtb {
        background: #101010 !important;
    }
    #hdtb, #hdtb.hdtba #hdtbMenus {
        border-bottom-color: #101010 !important;
    }

    /* Visited links */

    /* Titles (blue) #7EB3F3 */
    .rllt__action-button:visited, #gsr .rllt__action-button:active, ._eMi, #sbsb_f, #sbsb_f:hover, a:link, .w, #prs a:visited, #prs a:active, .q:active, .q:visited, .kl:active, .tbotu, a.iu-card-header, a.iu-card-header:hover, a.iu-card-header:visited {
        color: #9BAED2;
    }
    ._tX, ._Qqb._tX.ellip, ._Icb > a, .ab_dropdownlnkinfo:hover {
        color: #7EB3F3 !important;
    }

    /* Links (blue) #89A2CF*/
    a.iu-card-header, a.iu-card-header:hover, a.iu-card-header:visited, a.fl:link, .fl a, .flt, a.flt, .gl a:link, a.mblink, .mblink b, #foot #navcnt a, a.fl.q._KCd._tWc, .st .f .fl, div.duf3d a, ._YAp, .lr_dct_more_btn, a ._l6b {
